<template>
	<view class="wyh_table">
		<view class="fixed_left" :class="{rightBorder:rightBorder}">
			<view class="th" style="background-color: #F6F6F7">
				<view class="td tick" v-if="select" v-html="selectStr"></view>
				<template v-for="(th, ind) in thList">
					<view class="td" :class="{active:nSortKey==th.sortKey&&th.sortKey}" v-if="th.fixed" :key="ind" :style="{minWidth: th.width||'250rpx'}"  @click="bindSort(th.sortKey)">
						<view v-html="th.text"></view>
						<view class="ic" :class="nSort" v-if="th.sortKey"></view>
					</view>
				</template>
			</view>
			<view class="tr" v-for="(item, ind) in list" :key="ind" @click="links?bindLinks(item):bindClick(item)" :style="{backgroundColor:item.name==='小计'?'#F2FFF0':'white'}">
				<view class="td tick" :class="{checked:item.checked,disabled:item.disabled}" @click.stop="bindSelect(ind)" v-if="select"></view>
				<template v-for="(th, index) in thList">
					<view class="td" v-if="th.fixed" :key="index" :style="{minWidth: th.width||'250rpx'}">
						<view class="mark" v-if="th.markKey && item[th.markKey]">{{item[th.markKey]}}</view>
						<view class="txt">{{ th.dataKey == 'index'?ind+1 : item[th.dataKey] }}</view>
					</view>
				</template>
			</view>
		</view>
		<view class="scroll_right">
			<scroll-view scroll-x="true">
				<view class="th">
					<template v-for="(th, ind) in thList">
						<view class="td" :class="{active:nSortKey==th.sortKey&&th.sortKey}" v-if="!th.fixed" :key="ind" :style="{width: th.width||'250rpx'}" @click="bindSort(th.sortKey)" style="background-color: #F6F6F7;padding: 0 40rpx !important;">
							<view v-html="th.text"></view>
							<view class="ic" :class="nSort" v-if="th.sortKey"></view>
						</view>
					</template>
				</view>
				<view class="tr" :class="{disabled:item.disabled}" v-for="(item, ind) in list" :key="ind" @click="links?bindLinks(item):bindClick(item)">
					<template v-for="(th, index) in thList">
						<view class="td" v-if="!th.fixed" :key="index" :style="{width: th.width||'250rpx',backgroundColor:item.name==='小计'?'#F2FFF0':'white'}">
							<view class="mark" v-if="th.markKey && item[th.markKey]" :style="setColor(item,th)">{{item[th.markKey]}}</view>
							<!-- <view v-if="th.markKey && item[th.markKey]" v-html="item[th.markKey]"></view> -->
							<input class="val" :type="th.inputType" v-model="item[th.dataKey]" :placeholder="th.inputPlaceholder" @confirm="bindConfirm(ind)" @input="(e)=>bindInput(e,ind,th.dataKey)"  v-if="th.isInput" :style="{width: th.inputWidth||'80rpx', height: th.inputHeight||'44rpx', fontSize: th.inputSize||'28rpx', color: th.inputColor||'#333'}"/>
							<!-- <switch :checked="item[th.dataKey]" v-if="th.isSwitch"/> -->
							<evan-switch v-model="item[th.dataKey]" :active-value="true" :inactive-value="false" inactive-color="#C5C5C5" :size="20" v-if="th.isSwitch" @change="switchChange(item,th)"></evan-switch>
							<!-- <uni-easyinput class="val" :type="th.inputType" v-model="th.unit" :placeholder="th.inputPlaceholder" :clearable="th.inputClearable" v-if="th.isInput"/> -->
							<text v-else v-html="getHtml(item,th)" :style="setColor(item,th)"></text>
							<text class="unit" v-if="th.unit">{{th.unit}}</text>
						</view>
					</template>
				</view>
			</scroll-view>
		</view>
	</view>
</template>

<script>
	import evanSwitch from "@/components/evan-switch/evan-switch.vue"
	export default {
		name:"wyh-table",
		components:{
			evanSwitch
		},
		props: {
			// 表头
			thList: {
				type: Array,
				default: () => { return []}
			},
			// 是否可选
			select: {
				type: String,
				default: ''     //空  不可选    radio   单选   multiple   多选
			},
			// 选择列表头
			selectStr: {
				type: String,
				default: '选择'
			},
			// 列表数据
			items: {
				type: Array,
				default: () => { return []}
			},
			// 默认选中项id
			activeIds: {
				type: Array,
				default: () => { return []}
			},
			// 排序
			sort: {
				type: String,
				default: 'desc' 	//desc 降序    acs 升序
			},
			//排序字段
			sortKey: {
				type: String,
				default: ''			//排序关键词
			}, 
			// 是否可点击
			isClick: {
				type: Boolean,
				default: false
			},
			// 路由页面 自主传参
			links: {
				type: String,
				default: ''
			},
			// 路由参数 多个用,隔开
			linksKey: {
				type: String,
				default: ''
			},
			// 是否显示固定区与滚动区的分隔线
			rightBorder: {
				type: Boolean,
				default: false
			}
		},
		data() {
			return {
				list: this.items,
				nSort: this.sort,
				nSortKey: this.sortKey
			};
		},
		watch: {
			items: {
				handler(ret, oldValue) {
					this.list = ret;
				},
				deep: true //深度侦听
			},
			sort: function (ret) {
				this.nSort = ret;
			},
			sortKey: function (ret) {
				this.nSortKey = ret;
			}
		},
		
		methods: {
			getHtml(item,th){
				if(item[th.dataKey].indexOf('/')>-1&&th.isColor){
					return item[th.dataKey].split('/').slice(0,-1).join('/')
				}else{
					return th.dataKey == 'index'?ind+1 : item[th.dataKey]
				}
			},
			switchChange(item,th){
				this.$emit('switch-change',item,th)
			},
			//每一个单元格字段变颜色dataKey:'20/20/true'
			setColor(item,th){
				item[th.dataKey]=String(item[th.dataKey])
				if(item[th.dataKey].indexOf('/')>-1&&th.isColor){
					return {color:item[th.dataKey].split('/')[item[th.dataKey].split('/').length-1]==='true'?'green':'red'}
				}else{
					return {color:'black'}
				}
			},
			// 排序
			bindSort(sortKey) {
				if(!sortKey) return false;
				if(this.nSortKey == sortKey) {
					this.nSort = this.nSort == 'desc' ? 'acs' : 'desc';
				} else {
					this.nSortKey = sortKey;
					this.nSort = 'desc';
				}
				let sortData = {
					sort: this.nSort,
					sortKey: this.nSortKey
				};
				this.$emit('onSort', sortData);
			},
			// 选择
			bindSelect(ind) {
				let that = this;
				let _list = this.list;
				_list.map(function (item, i) {
					if( that.select=='radio') {
						if(i == ind) {
							item.checked = !item.checked;
						} else {
							item.checked = false;
						}
					} else {
						if(i == ind) {
							item.checked = !item.checked;
						}
					}
				})
				this.list = [];
				this.list = _list;
				this.$emit('onSelect',this.list[ind]);
			},
			//输入confirm事件
			bindConfirm(ind){
				let _list = this.list;
				let obj = this.list[ind];
				this.$emit('onSetting',obj);
			},
			//输入input事件
			bindInput(e,ind,itemKey){
				let _list = this.list;
				// let obj = this.list[ind];
				// e: input $event事件，ind: 整行对象的index，itemKey: 修改的这一行的key
				this.$emit('onInput',e, ind, itemKey);
			},
			bindClick(item) {
				if(this.isClick) {
					this.$emit('onClick', item);
				}
			},
			bindLinks(items){
				if(this.links&&this.linksKey) {
					if(this.linksKey.indexOf(',')!=-1){
						let keylist = this.linksKey.split(',');
						let keyTxt = '';
						for (var i = 0; i < keylist.length; i++) {
							if(i==0){
								keyTxt = '?'+keylist[i]+'='+items[keylist[i]]
							}else{
								keyTxt = keyTxt+'&'+keylist[i]+'='+items[keylist[i]]
							}
						}
						this.$loginOpenWin(this.links+keyTxt)
					}else{
						this.$loginOpenWin(this.links+'?'+this.linksKey+'='+items[this.linksKey])
					}
				}
			}
		},
		created() {
			let that = this;
			if(this.activeIds.length) {
				this.list.map(function (item) {
					if(that.activeIds.indexOf(item.id) != -1) {
						item.checked = true;
					}
				})
			}
		}
	}
</script>

